
function showVideo(videoSrc, day, hour, minute) {
    const now = new Date();
    const currentDay = now.getDate();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();

    if (currentDay > day || (currentDay === day && (currentHour > hour || (currentHour === hour && currentMinute >= minute)))) {
        // Masquer la vidéo en cours, s'il y en a une
        const videoContainer = document.getElementById('videoContainer');
        videoContainer.innerHTML = ''; // Retire tout contenu précédent

        // Crée un nouvel élément vidéo et l'ajoute dans le conteneur
        const video = document.createElement('video');
        video.controls = true;
        video.innerHTML = `<source src="${videoSrc}" type="video/mp4">Votre navigateur ne supporte pas le format vidéo.`;
        video.style.width = '100%'; // Applique des styles si nécessaire
        videoContainer.appendChild(video);

        // Affiche le conteneur
        videoContainer.style.display = 'block';
    } else {
        alert("Désolé, cette vidéo n'est pas encore disponible.");
    }
}



